def get_stats(ids, counts=None):
    if counts is None:
        counts = {}

    for i in range(len(ids) - 1):
        pair = (ids[i], ids[i + 1])
        if pair in counts:
            counts[pair] += 1
        else:
            counts[pair] = 1

    return counts
